function suiMobile(){let dependancies=document.querySelectorAll('.su-toggle--bp');for(let dependancy of dependancies){toggleClassOnBreakPoint(dependancy)}}
function toggleClassOnBreakPoint(element){let allInfo=element.dataset.info;if(!allInfo.includes('.')){allInfo=element.dataset.info+'.'}
allInfo=allInfo.split('.');for(let info of allInfo){let entries=info.split('#');let value,type,className=entries[1];if(entries[0].includes("min")){type="min-width";value=entries[0].replace("min-","")}else if(entries[0].includes("max")){type="max-width";value=entries[0].replace("max-","")}
console.log(value+", "+type+", "+className);mediaQuery(element,type,value,className)}}
function mediaQuery(el,type,value,className){function applyMedia(x){if(x.matches){el.classList.add(className)}else{if(el.classList.contains(className)){el.classList.remove(className)}}}
var x=window.matchMedia("("+type+":"+value+")");applyMedia(x);x.addListener(applyMedia)}
suiMobile();window.addEventListener('resize',suiMobile);let toggleClassName=document.querySelectorAll('.su-toggle-class');for(let each of toggleClassName){let trigger="click";let allInfo=each.dataset.toggle;if(!allInfo.includes('.')){allInfo=each.dataset.toggle+'.'}
allInfo=allInfo.split('.');toggleClass(each,allInfo)}
function toggleClass(trigger,allInfo){for(let each of allInfo){console.log(each)
let items=each.split('|');if(items[0]==""||items[0]==undefined){return}
let el='#'+items[0];let className=items[1];let state=items[2];trigger.addEventListener(state,function(){document.querySelector(el).classList.toggle(className)})}}
